mapper.select:
SELECT POST_DECLARATION_ID DECLARATION_ID, POST_DECLARATION_DATE DECLARATION_DATE, POST_DECLARATION_CONTENT DECLARATION_CONTENT, USER_ID,NICKNAME, POST_DECLARATION_CHECK DECLARATION_CHECK, POST_ID type_id, POST_CATE CATE, POST_PROCESS_DATE DECL_PROCESS_DATE FROM (SELECT ROWNUM AS RNUM, POST_DECLARATION_ID, POST_DECLARATION_DATE, POST_DECLARATION_CONTENT, USER_ID,NICKNAME, POST_DECLARATION_CHECK, POST_ID, POST_CATE, POST_PROCESS_DATE FROM (SELECT POST_DECLARATION_ID, POST_DECLARATION_DATE, POST_DECLARATION_CONTENT, S.USER_ID, USER_NICKNAME NICKNAME, POST_DECLARATION_CHECK, POST_ID, POST_CATE, POST_PROCESS_DATE FROM (SELECT POST_DECLARATION_ID, POST_DECLARATION_DATE, POST_DECLARATION_CONTENT, USER_ID, POST_DECLARATION_CHECK, POST_ID, POST_CATE, POST_PROCESS_DATE FROM TBL_POST_DECLARATION
POST_DECLARATION_CONTENT LIKE '%' || #{selectDecl.declarationContent} || '%'
AND TO_CHAR(POST_DECLARATION_DATE, 'YYYY-MM-DD') BETWEEN #{selectDecl.startDeclarationDate} AND #{selectDecl.endDeclarationDate}
AND TO_CHAR(POST_PROCESS_DATE, 'YYYY-MM-DD') BETWEEN #{selectDecl.startPocecssDate} AND #{selectDecl.endPocecssDate}
AND POST_CATE = #{selectDecl.declarationType}
AND POST_DECLARATION_CHECK = #{selectDecl.processType}
) S JOIN TBL_USER U ON S.USER_ID = U.USER_ID ORDER BY POST_DECLARATION_ID)
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}) WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

SELECT COMMENT_DECLARATION_ID DECLARATION_ID, COMMENT_DECLARATION_DATE DECLARATION_DATE, COMMENT_DECLARATION_CONTENT DECLARATION_CONTENT, USER_ID, nickname, COMMENT_DECLARATION_CHECK DECLARATION_CHECK, COMMENT_ID type_id, COMMENT_CATE CATE, COMMENT_PROCESS_DATE DECL_PROCESS_DATE FROM (SELECT ROWNUM AS RNUM, COMMENT_DECLARATION_ID, COMMENT_DECLARATION_DATE, COMMENT_DECLARATION_CONTENT, USER_ID, NICKNAME,COMMENT_DECLARATION_CHECK, COMMENT_ID, COMMENT_CATE, COMMENT_PROCESS_DATE FROM (SELECT COMMENT_DECLARATION_ID, COMMENT_DECLARATION_DATE, COMMENT_DECLARATION_CONTENT, S.USER_ID, USER_NICKNAME NICKNAME, COMMENT_DECLARATION_CHECK, COMMENT_ID, COMMENT_CATE, COMMENT_PROCESS_DATE FROM(SELECT COMMENT_DECLARATION_ID, COMMENT_DECLARATION_DATE, COMMENT_DECLARATION_CONTENT, USER_ID, COMMENT_DECLARATION_CHECK, COMMENT_ID, COMMENT_CATE, COMMENT_PROCESS_DATE FROM TBL_COMMENT_DECLARATION
COMMENT_DECLARATION_CONTENT LIKE '%' || #{selectDecl.declarationContent} || '%'
AND TO_CHAR(COMMENT_DECLARATION_DATE, 'YYYY-MM-DD') BETWEEN #{selectDecl.startDeclarationDate} AND #{selectDecl.endDeclarationDate}
AND TO_CHAR(COMMENT_PROCESS_DATE, 'YYYY-MM-DD') BETWEEN #{selectDecl.startPocecssDate} AND #{selectDecl.endPocecssDate}
AND COMMENT_CATE = #{selectDecl.declarationType}
AND COMMENT_DECLARATION_CHECK = #{selectDecl.processType}
) S JOIN TBL_USER U ON S.USER_ID = U.USER_ID ORDER BY COMMENT_DECLARATION_ID)
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}) WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

SELECT FEED_DECLARATION_ID DECLARATION_ID, FEED_DECLARATION_DATE DECLARATION_DATE, FEED_DECLARATION_CONTENT DECLARATION_CONTENT, USER_ID, FEED_DECLARATION_CHECK DECLARATION_CHECK,FEED_ID type_id, FEED_PROCESS_DATE DECL_PROCESS_DATE FROM (SELECT ROWNUM AS RNUM, FEED_DECLARATION_ID, FEED_DECLARATION_DATE, FEED_DECLARATION_CONTENT, USER_ID, FEED_DECLARATION_CHECK, FEED_ID, FEED_PROCESS_DATE FROM (SELECT FEED_DECLARATION_ID, FEED_DECLARATION_DATE, FEED_DECLARATION_CONTENT, USER_ID, FEED_DECLARATION_CHECK, FEED_ID, FEED_PROCESS_DATE FROM TBL_FEED_DECLARATION
FEED_DECLARATION_CONTENT LIKE '%' || #{selectDecl.declarationContent} || '%'
AND TO_CHAR(FEED_DECLARATION_DATE, 'YYYY-MM-DD') BETWEEN #{selectDecl.startDeclarationDate} AND #{selectDecl.endDeclarationDate}
AND TO_CHAR(FEED_PROCESS_DATE, 'YYYY-MM-DD') BETWEEN #{selectDecl.startPocecssDate} AND #{selectDecl.endPocecssDate}
AND FEED_DECLARATION_CHECK = #{selectDecl.processType}
ORDER BY FEED_DECLARATION_ID)
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}) WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

SELECT count(FEED_DECLARATION_ID)
FROM TBL_FEED_DECLARATION


        FEED_DECLARATION_CONTENT LIKE '%' || #{selectDecl.declarationContent} || '%'


        AND TO_CHAR(FEED_DECLARATION_DATE, 'YYYY-MM-DD') BETWEEN
        #{selectDecl.startDeclarationDate} AND #{selectDecl.endDeclarationDate}


        AND TO_CHAR(FEED_PROCESS_DATE, 'YYYY-MM-DD') BETWEEN
        #{selectDecl.startPocecssDate} AND #{selectDecl.endPocecssDate}


        AND FEED_DECLARATION_CHECK = #{selectDecl.processType}

SELECT count(POST_DECLARATION_ID)
FROM TBL_POST_DECLARATION


        POST_DECLARATION_CONTENT LIKE '%' || #{selectDecl.declarationContent} || '%'


        AND TO_CHAR(POST_DECLARATION_DATE, 'YYYY-MM-DD') BETWEEN
        #{selectDecl.startDeclarationDate} AND #{selectDecl.endDeclarationDate}


        AND TO_CHAR(POST_PROCESS_DATE, 'YYYY-MM-DD') BETWEEN
        #{selectDecl.startPocecssDate} AND #{selectDecl.endPocecssDate}


        AND POST_CATE = #{selectDecl.declarationType}


        AND POST_DECLARATION_CHECK = #{selectDecl.processType}

SELECT count(COMMENT_DECLARATION_ID)
FROM TBL_COMMENT_DECLARATION


        COMMENT_DECLARATION_CONTENT LIKE '%' || #{selectDecl.declarationContent} || '%'


        AND TO_CHAR(COMMENT_DECLARATION_DATE, 'YYYY-MM-DD') BETWEEN
        #{selectDecl.startDeclarationDate} AND #{selectDecl.endDeclarationDate}


        AND TO_CHAR(COMMENT_PROCESS_DATE, 'YYYY-MM-DD') BETWEEN
        #{selectDecl.startPocecssDate} AND #{selectDecl.endPocecssDate}


        AND COMMENT_CATE = #{selectDecl.declarationType}


        AND COMMENT_DECLARATION_CHECK = #{selectDecl.processType}
