SELECT USER_ID, USER_NAME, USER_LOGIN_ID, USER_PHONENUMBER, USER_GENDER, USER_AGE, USER_EMAIL, USER_ADDRESS, USER_ADDRESS_DETAIL, USER_ZIPCODE FROM ( SELECT ROWNUM AS RNUM, USER_ID, USER_NAME, USER_LOGIN_ID, USER_PHONENUMBER, USER_GENDER, USER_AGE, USER_EMAIL, USER_ADDRESS, USER_ADDRESS_DETAIL, USER_ZIPCODE FROM ( SELECT U.USER_ID, USER_NAME, USER_LOGIN_ID, USER_PHONENUMBER, USER_GENDER, USER_AGE, USER_EMAIL, USER_ADDRESS, USER_ADDRESS_DETAIL, USER_ZIPCODE FROM TBL_USER U LEFT JOIN ( SELECT TBL_ATTENDANCE.USER_ID, MAX(ATTENDANCE_DATE) MAXD FROM TBL_ATTENDANCE GROUP BY USER_ID ) A ON U.USER_ID = A.USER_ID
USER_NAME like '%' || #{selectUser.userInfoDate} || '%' USER_LOGIN_ID like '%' || #{selectUser.userInfoDate} || '%' USER_EMAIL like '%' || #{selectUser.userInfoDate} || '%'
AND TO_CHAR(USER_SUB_DATE, 'YYYY-MM-DD') BETWEEN #{selectUser.startJoinDate} AND #{selectUser.endJoinDate}
AND USER_AGE BETWEEN #{selectUser.startUserAge} AND #{selectUser.endUserAge}
AND USER_GENDER = #{selectUser.gender}
AND TO_CHAR(A.MAXD, 'YYYY-MM-DD') BETWEEN #{selectUser.startVisitDate} AND #{selectUser.endVisitDate}
AND USER_LOCK = 'N' ORDER BY USER_ID)
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}) WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

SELECT COUNT(USER_ID)
    FROM (SELECT U.USER_ID, USER_NAME, USER_LOGIN_ID, USER_PHONENUMBER, USER_GENDER,
USER_AGE, USER_EMAIL, USER_ADDRESS, USER_ADDRESS_DETAIL, USER_ZIPCODE
FROM TBL_USER U LEFT JOIN (
SELECT TBL_ATTENDANCE.USER_ID, MAX(ATTENDANCE_DATE) MAXD FROM TBL_ATTENDANCE GROUP BY USER_ID
) A
ON U.USER_ID = A.USER_ID



             USER_NAME like '%' || #{selectUser.userInfoDate} || '%' 
             USER_LOGIN_ID like '%' || #{selectUser.userInfoDate} || '%' 
             USER_EMAIL like '%' || #{selectUser.userInfoDate} || '%' 



        AND TO_CHAR(USER_SUB_DATE, 'YYYY-MM-DD') BETWEEN #{selectUser.startJoinDate} AND #{selectUser.endJoinDate}


        AND USER_AGE BETWEEN #{selectUser.startUserAge} AND #{selectUser.endUserAge}


        AND USER_GENDER = #{selectUser.gender}


        AND TO_CHAR(A.MAXD, 'YYYY-MM-DD') BETWEEN #{selectUser.startVisitDate} AND #{selectUser.endVisitDate}


AND USER_LOCK = 'N'
ORDER BY USER_ID)
